import React from 'react';

interface Istate{
    count:number
}


export default class Video extends React.Component<any,Istate>{
    public constructor(props:any) {
      super(props);
      this.clickHandler = this.clickHandler.bind(this);
    }

    public state:Readonly<Istate>={
        count:0
    }
    
    public clickHandler(){
        var file = (document.getElementById("videofile") as HTMLInputElement).files
        var video = document.getElementById("videoplay") as HTMLVideoElement
        if(file !== null && file.length !== 0){
            var src = window.URL.createObjectURL(file[this.state.count])
            console.log(file[0])
            console.log(src)
            // video.src = src
            this.setState({
                count:this.state.count+1
            })
            console.log(this.state.count);
            
        }
        else{
            alert("无上传文件")
        }
        //     count:this.state.count+1,
        // })
        // console.log(this.state.count);
    }

    public loopPLAY(){
        var file = (document.getElementById("videofile") as HTMLInputElement).files
        var video = document.getElementById("videoplay") as HTMLVideoElement
        if (file !== null && file.length !== 0 && this.state.count<file.length){
            var src = window.URL.createObjectURL(file[0])
            video.load()
            video.play()
        }
    }


    public render() {
      return (
        <div>
            <div>
                {/* 读取本地文件并播放 */}
                <input id="videofile" type="file" multiple/>
                <input type="button" id="btplay" onClick={this.clickHandler} value="播放"/>    
            </div>

            <div>
                <meta name="viewport" content="width=device-width, initial-scale=1.0"/>
                <video controls autoPlay id="videoplay" width={"100%"} onEnded={this.loopPLAY}>
                    <source type="audio/mpeg"/>
                </video> 
            </div>
        </div>
      );
    }
  }
