import React from 'react';

interface Istate{
    num:number           //后台获取的列表中视频数量
    count:number         //目前播放到第几个视频
}

interface Iprops{
    file:any
}


export default class VideoBox extends React.Component<Iprops,Istate>{
    public constructor(props:any) {
      super(props);
      this.loopPLAY = this.loopPLAY.bind(this);
    }

    public state:Readonly<Istate>={
        num:3,
        count:1
    }

    public loopPLAY(){
        var file = this.props.file
        var videoid = "videoplay"+(this.state.count+1)
        var video = document.getElementById(videoid) as HTMLVideoElement
        if (file !== null && file.length !== 0 && this.state.count<file.length){
            video.load()
            video.play()
        }
    }

    public render() {
        for(var i=0;i<this.props.file.length;i++){
            return (
                <li id="videobox1" style={{margin:"0",display:"inline-block", marginLeft:"10px" ,marginRight:"10px", width:"100%"}}>
                    <video controls autoPlay id="videoplay1" width={"100%"}>
                    <source src={this.props.file[i]} type="audio/mpeg"/>
                </video>
                </li>
              );
        }
    }
  }
