    public clickHandler(){
        // var file =(document.getElementById("fileinput") as HTMLInputElement).value
        // var formData = new FormData(); //创建表单数据对象
        // formData.append('file',file); //将文件添加到表单对象中
        // console.log(file);
        
        // fetch("http://localhost:3000",{       //传输
        //     method :"post",
        //     // body: formData,
        //     headers:{
        //         "Content-Type": "multipart/form-data"
        //     }
        // }) 
        // .then((d:any)=>{
        //     var percent = (d.loaded / d.total) * 100 + '%';
        //     let progress = (document.getElementById("progress") as HTMLDivElement)
        //     console.log(percent);
        //     progress.style.width = percent;
        //     progress.textContent = percent;
        //     console.log('result is',d);
        // })

        var oloaded:any
        var xhr = new XMLHttpRequest();
        var ot:any
        
        function UpladFile() {
            var fileObj = (document.getElementById("fileinput") as HTMLInputElement).value; // js 获取文件对象
            var url =  "http://localhost:3000"; // 接收上传文件的后台地址

            var form = new FormData(); // FormData 对象
            form.append("file", fileObj); // 文件对象

            xhr = new XMLHttpRequest();  // XMLHttpRequest 对象
            xhr.open("post", url, true); //post方式，url为服务器请求地址，true 该参数规定请求是否异步处理。
            xhr.onload = uploadComplete; //请求完成
            xhr.onerror =  uploadFailed; //请求失败

            xhr.upload.onprogress = progressFunction;//【上传进度调用方法实现】
            xhr.upload.onloadstart = function(){//上传开始执行方法
                ot = new Date().getTime();   //设置上传开始时间
                oloaded = 0;//设置上传开始时，以上传的文件大小为0
            };

            xhr.send(form); //开始上传，发送form数据
        }

        //上传成功响应
        function uploadComplete(evt:any) {
            //服务断接收完文件返回的结果

            var data = JSON.parse(evt.target.responseText);
            if(data.success) {
                alert("上传成功！");
            }else{
                alert("上传失败！");
            }

        }
        //上传失败
        function uploadFailed(evt:any) {
            alert("上传失败！");
        }
        //取消上传
        function cancleUploadFile(){
            xhr.abort();
        }


        //上传进度实现方法，上传过程中会频繁调用该方法
        function progressFunction(evt:any) {
            var progressBar = document.getElementById("progressBar") as HTMLProgressElement;
            var percentageDiv = document.getElementById("percentage") as HTMLSpanElement;
            // event.total是需要传输的总字节，event.loaded是已经传输的字节。如果event.lengthComputable不为真，则event.total等于0
            if (evt.lengthComputable) {//
                progressBar.max = evt.total;
                progressBar.value = evt.loaded;
                percentageDiv.innerHTML = Math.round(evt.loaded / evt.total * 100) + "%";
            }
            var time = document.getElementById("time");
            var nt = new Date().getTime();//获取当前时间
            var pertime = (nt-ot)/1000; //计算出上次调用该方法时到现在的时间差，单位为s
            let ot = new Date().getTime(); //重新赋值时间，用于下次计算
            var perload = evt.loaded - oloaded; //计算该分段上传的文件大小，单位b
            oloaded = evt.loaded;//重新赋值已上传文件大小，用以下次计算
            //上传速度计算
            var speed = perload/pertime;//单位b/s
            var bspeed = speed;
            var units = 'b/s';//单位名称
            if(speed/1024>1){
                speed = speed/1024;
                units = 'k/s';
            }
            if(speed/1024>1){
                speed = speed/1024;
                units = 'M/s';
            }
            speed = speed.toFixed(1);
            //剩余时间
            var resttime = ((evt.total-evt.loaded)/bspeed).toFixed(1);
            time.innerHTML = '，速度：'+speed+units+'，剩余时间：'+resttime+'s';
            if(bspeed==0) time.innerHTML = '上传已取消';
        }
    }

    
    public setForm(){
        // let sumitForm=new FormData
        // let form = (document.getElementById("uploadForm") as HTMLFormElement)        
        let file = (document.querySelector('[type=file]') as HTMLInputElement)
        // 新建FormData对象
        let formData = new FormData();
        // 取得input标签中的文件。注：input标签中的文件可以选择多个
        formData.append('upload', file.value);
        //创建XMLHttpRequest，用这个来发送数据
        var xhr = new XMLHttpRequest();
        /*初始化HTTP请求参数(请求方式,url,是否同步)*/
        xhr.open('post', './');
        
        /*
        xmlhttp.setRequestHeader("Content-type","application/x-www-form-urlencoded");
        设置请求头的contentType
        */
        
        // 监听上传进度
        xhr.upload.onprogress = function (ev) {
        // 事件对象
        // console.log(ev);
            var percent = (ev.loaded / ev.total) * 100 + '%';
            let progress = (document.getElementById("progress") as HTMLDivElement)
            console.log(percent);
            progress.style.width = percent;
            progress.textContent = percent;
        }

        xhr.send(formData);

        xhr.onreadystatechange = function () {
        //status状态为200 表示请求成功，readystate为4表示对象传递完毕或者准备就绪 
                if(xhr.readyState === 4 && xhr.status === 200) {
                }
        }
    }
    
    public render() {
      return (
        <div>
            {/* <form action=""  target="" method="get" encType="multipart/form-data" id="uploadForm" onSubmit={this.submitHandler}> */}
            <input id="fileinput" type="file" multiple={true}/>
            {/* <input type="submit" value="上传" onClick={this.clickHandler} onSubmit={this.clickHandler}/> */}
            <input type="submit" value="上传" onClick={this.clickHandler}/>
            {/* </form> */}
            <label >上传进度：</label>
            {/* <div id="progress" style={{width:0, backgroundColor:"gray"}}>进度</div> */}
            <progress id="progressBar" value="0" max="100" style={{width:300}}></progress>
            <span id="percentage"></span><span id="time"></span>
            {/* <video src={this.state.fdir[0]} controls={true} preload="auto" autoPlay> */}
                {/* <source src="../2.mp4"  type="video/webm"/> */}
                {/* 您的浏览器不支持播放该视频！ */}
           {/* </video> */}
        </div>
      );
    }
  }
