import React from 'react';
import VideoBox from './videobox';

interface Istate{
    num:number           //后台获取的列表中视频数量
    count:number         //目前播放到第几个视频
}


export default class Video extends React.Component<any,Istate>{
    public constructor(props:any) {
      super(props);
      this.clickHandler = this.clickHandler.bind(this);
      this.loopPLAY = this.loopPLAY.bind(this);
    }

    public state:Readonly<Istate>={
        num:3,
        count:1
    }
    
    public clickHandler(){
        var file = (document.getElementById("videofile") as HTMLInputElement).files
        if (file !== null && file.length !== 0){

        }
        else{
            alert("无上传文件")
        }
    }

    public loopPLAY(){
        var file = (document.getElementById("videofile") as HTMLInputElement).files
        var videoid = "videoplay"+(this.state.count+1)
        var video = document.getElementById(videoid) as HTMLVideoElement
        if (file !== null && file.length !== 0 && this.state.count<file.length){
            video.load()
            video.play()
        }
    }

    public render() {
      return (
        <div>
            <div>
                {/* 读取本地文件并播放 */}
                <p>请在下方同时选中所有本地视频：</p>
                <input id="videofile" type="file" multiple/>
                <input type="button" id="btplay" onClick={this.clickHandler} value="播放"/>    
            </div>
            <VideoBox file={this.state.file}/>
        </div>
      );
    }
  }
