import React from 'react';


interface Istate{
    count:number
    fdir:string
}

export default class Video extends React.Component<any,Istate>{
    public constructor(props:any) {
      super(props);
      this.clickHandler = this.clickHandler.bind(this);
    }
    
    public state:Readonly<Istate>={
        count:0,
        fdir:""
    }

    public clickHandler(){
        let a =(document.getElementById("fileinput") as HTMLInputElement).value  //ts必须进行强制转换才能读取vlaue
        // let tempFdir = this.state.fdir
        let tempCount = this.state.count+1
        // tempFdir.push(a)
        // tempFdir = a
        // console.log(tempCount,tempFdir);
        // console.log(this.state.count,this.state.fdir)
        this.setState({
            count:tempCount,
            fdir:a   
        }) 
        alert(this.state.count) 
        alert(this.state.fdir) 
        
    }

    public render() {
      return (
        <div>
            <form action=""  target="" method="get" encType="multipart/form-data" id="uploadForm">
                 <input id="fileinput" type="file" multiple={true} />
                 <input type="submit" value="上传" onClick={this.clickHandler}/>
            </form>
            <video src={this.state.fdir} controls={true} preload="auto" autoPlay>
                {/* <source src="../2.mp4"  type="video/webm"/> */}
                您的浏览器不支持播放该视频！
           </video>
        </div>
      );
    }
  }
