import React from 'react';

interface Istate{
    count:number
}


interface Iprops{
    fdir:string
}

export default class Video extends React.Component<Iprops,Istate>{
    public constructor(props:any) {
      super(props);
      this.clickHandler = this.clickHandler.bind(this);
    }

    public state:Readonly<Istate>={
        count:0
    }
    
    public clickHandler(){
        var file = document.getElementById("videofile") as HTMLInputElement
        console.log(file.files);
        if(file.files?.length != 0){
            var src = window.URL.createObjectURL(file.files[0])
            console.log(src)
        }
        else{
            alert("无上传文件")
        }
        //     count:this.state.count+1,
        // })
        // console.log(this.state.count);
    }


    public render() {
      return (
        <div>
            <div>
                <input id="videofile" type="file"/>
                <input type="button" id="btplay" onClick={this.clickHandler} value="播放"/>
            </div>
            <div>
                <video controls autoPlay id="videoplay">
                    <source src={this.props.fdir} type="audio/mpeg"/>
                </video> 
            </div>
        </div>
      );
    }
  }
