import React from 'react';

interface Istate{
    count:number
}


interface Iprops{
    fdir:string
    fid:string
}

export default class Video extends React.Component<Iprops,Istate>{
    public constructor(props:any) {
      super(props);
      this.play = this.play.bind(this);
    }

    public state:Readonly<Istate>={
        count:0
    }
    
    public play(){
        var file = document.getElementById(this.props.fid) as HTMLInputElement
        var src=window.URL.createObjectURL(file.files[0])
        //     count:this.state.count+1,
        // })
        // console.log(this.state.count);
    }


    public render() {
      return (
        <div>
            <div>
                <video controls autoPlay id="videoplay">
                    <input id="fileinput" type="file" multiple={true}/>
                    <input type="button" onClick={this.play} value="播放" />
                    <source src={"this.props.fdir"} type="audio/mpeg"/>
                </video> 
            </div>
        </div>
      );
    }
  }
