import React from 'react';
import VideoBox from './videobox';

interface Istate{
    num:number           //后台获取的列表中视频数量
    count:number         //目前播放到第几个视频
    file:any
}


export default class Video extends React.Component<any,Istate>{
    public constructor(props:any) {
      super(props);
      this.clickHandler = this.clickHandler.bind(this);
    }

    public state:Readonly<Istate>={
        num:3,
        count:1,
        file:null
    }
    
    public clickHandler(){
        var filelist = (document.getElementById("videofile") as HTMLInputElement).files
        if (filelist !== null && filelist.length !== 0){
            this.setState({
                file:filelist
            })
        }
        else{
            alert("无上传文件")
        }
    }

    public render() {
      return (
        <div>
            <div>
                {/* 读取本地文件并播放 */}
                <p>请在下方同时选中所有本地视频：</p>
                <input id="videofile" type="file" multiple/>
                <input type="button" id="btplay" onClick={this.clickHandler} value="播放"/>    
            </div>
            <VideoBox file={this.state.file}/>
        </div>
      );
    }
  }
