import React from 'react';

interface Istate{
    count:number
}


export default class Video extends React.Component<any,Istate>{
    public constructor(props:any) {
      super(props);
      this.clickHandler = this.clickHandler.bind(this);
      this.loopPLAY = this.loopPLAY.bind(this);
    }

    public state:Readonly<Istate>={
        count:1
    }
    
    public clickHandler(){
        var file = (document.getElementById("videofile") as HTMLInputElement).files
        if(file !== null && file.length !== 0){
            for(var i=1;i<=file.length;i++){
                var id="videoplay"+i
                var video = document.getElementById(id) as HTMLVideoElement
                var src = window.URL.createObjectURL(file[i-1])
                console.log(file[i-1])
                video.src = src
            }
        }
        else{
            alert("无上传文件")
        }
    }

    public loopPLAY(){
        var file = (document.getElementById("videofile") as HTMLInputElement).files
        var videoid = "videoplay"+(this.state.count+1)
        var video = document.getElementById(videoid) as HTMLVideoElement
        if (this.state.count<3){
            this.setState({
                count:this.state.count+1
            })
            console.log(this.state.count)
            video.load()
            video.play()
        }
    }

    public render() {
      return (
        <div>
            <div>
                {/* 读取本地文件并播放 */}
                <p>请在下方同时选中所有本地视频：</p>
                <input id="videofile" type="file" multiple/>
                <input type="button" id="btplay" onClick={this.clickHandler} value="播放"/>    
            </div>
            <div>
                <ul id="videobox" style={{overflowX:"auto", listStyle:"none", width:"100%", whiteSpace:"nowrap", position:"absolute"}}>
                    <li id="videobox1" style={{margin:"0",display:"inline-block", marginLeft:"10px" ,marginRight:"10px", width:"100%"}}> 
                        <video controls autoPlay id="videoplay1" width={"100%"} onEnded={this.loopPLAY}>
                            <source type="audio/mpeg"/>
                        </video>
                    </li>
                    <li id="videobox2" style={{display:"inline-block", marginLeft:"10px" ,marginRight:"10px", width:"100%"}}>
                        <video controls id="videoplay2" width={"100%"} onEnded={this.loopPLAY}>
                            <source type="audio/mpeg"/>
                        </video>
                    </li>
                    <li id="videobox3" style={{display:"inline-block",marginLeft:"10px" ,marginRight:"10px", width:"100%"}}>
                        <video controls id="videoplay3" width={"100%"} onEnded={this.loopPLAY}>
                            <source type="audio/mpeg"/>
                        </video>
                    </li>
                </ul>
            </div>
        </div>
      );
    }
  }
