import React from 'react';

interface Istate{
    count:number
    fdir?:any
}

export default class Video extends React.Component<any,Istate>{
    public constructor(props:any) {
      super(props);
    }
    
    public state:Readonly<Istate>={
        count:0,
        fdir:0
    }

    public clickHandler(){
        // this.setState({
        //     count:this.state.count+1,
        // })
        // console.log(this.state.count);
        var a =(document.getElementById("fileinput") as HTMLInputElement)  //ts必须进行强制转换才能读取vlaue
        alert(a.value)   
    }

    public render() {
      return (
        <div>
            <form action=""  method="post" id="uploadForm" encType="multipart/form-data">
                 <input id="fileinput" type="file"/>
                 {/* <input type="submit" value="上传"/> */}
                 <button onClick={this.clickHandler}>输出</button>
            </form>
            
        </div>
      );
    }
  }
