import React from 'react';
import Video from './video';

interface Istate{
    position:number
    width:number
    items:JSX.Element[]
}


export default class VideoBox extends React.Component<any,Istate>{
    public constructor(props:any) {
      super(props);
      this.clickHandler = this.clickHandler.bind(this);
      this.clickLeft = this.clickLeft.bind(this);
      this.clickRight = this.clickRight.bind(this);
    }

    public state:Readonly<Istate>={
        position:0,
        width:document.body.clientWidth,
        items:[]
    }
    
    public clickHandler(){
        var file = (document.getElementById("videofile") as HTMLInputElement).files
        if(file !== null && file.length !== 0){
            for(var i=1;i<=file.length;i++){
                var id="videoplay"+i
                var video = document.getElementById(id) as HTMLVideoElement
                var src = window.URL.createObjectURL(file[i-1])
                console.log(file[i-1])
                console.log(src)
                video.src = src
            }
        }
        else{
            alert("无上传文件")
        }
    }

    public clickLeft(){
        var id:number
        var ulist = document.getElementById("videobox") as HTMLUListElement
        if(this.state.position!==0){
            var mov=this.state.position-this.state.width
            this.setState({
                position:mov
            })
            ulist.scrollTo(mov,0)
            if(mov===0){
                id=1
            }
            else if(mov===this.state.width){
                id=2
            }
            else{
                id=3
            }
            var file = (document.getElementById("videofile") as HTMLInputElement).files
            if(file !== null && file.length !== 0){
                for(var i=1;i<4;i++){
                    var videoid = "videoplay"+i
                    var video = document.getElementById(videoid) as HTMLVideoElement
                    if(id===i){
                        video.play()
                    }
                    else{
                        video.pause()
                    }
                }
            }
        }
    }
    public clickRight(){
        var ulist = document.getElementById("videobox") as HTMLUListElement
        if(this.state.position!==this.state.width*2){
            var id:number
            var mov=this.state.position+this.state.width
            this.setState({
                position:mov
            })
            ulist.scrollTo(mov,0)
            if(mov===0){
                id=1
            }
            else if(mov===this.state.width){
                id=2
            }
            else{
                id=3
            }
            var file = (document.getElementById("videofile") as HTMLInputElement).files
            if(file !== null && file.length !== 0){
                for(var i=1;i<4;i++){
                    var videoid = "videoplay"+i
                    var video = document.getElementById(videoid) as HTMLVideoElement
                    if(id===i){
                        video.play()
                    }
                    else{
                        video.pause()
                    }
                }
            }
        }
    }

    public load(){
        var itemstemp=[]
        var autoplay:boolean
        var listLength=0
        var ip="http://127.0.0.1:8080/uploads/"
        listLength=getLength(ip)  //代替后台获取视频序列长度
        console.log(listLength);

        function getLength(ip:string){
            var cnt=1
            for(var i=1;i===cnt;i++){
                var src=ip+cnt
                console.log(src);
                if(isExist(src)===false){
                    console.log("return",cnt);
                }
                else{
                    cnt=cnt+1
                }
            }
            return cnt
        }

        function isExist(src:string){
            var xhr = new XMLHttpRequest()
            xhr.open("GET",src,false);
            console.log("unsended");
            
            xhr.send();
            console.log("SENDED");
            
            if(xhr.readyState===4){  
                if(xhr.status===200) 
                    return true; //url存在  
                else if(xhr.status===404) 
                    return false; //url不存在  
                else return false;//其他状态  
            }
        }
        
        // if(listLength!==0){
        //     for(var i=1;i<=listLength;i++){
        //         var id="videoplay"+i
        //         var src="http://127.0.0.1:8080/uploads/"+i
        //         if(this.isExist(src)){
        //              if(i===1){
        //                 autoplay=true
        //             }
        //             else{
        //                 autoplay=false
        //             }
        //             itemstemp.push(<Video key={i} id={id} autoplay={autoplay} src="src"/>)
        //         }
        //     }
        //     this.setState({
        //         items:itemstemp
        //     })
        // }
        
    }
    public render() {
        return (
            <div>
                <div>
                    {/* 读取本地文件并播放 */}
                    {/* <p>请在下方选择本地视频：</p>
                    <input id="videofile" type="file" multiple/>
                    <input type="button" id="btplay" onClick={this.clickHandler} value="播放"/>     */}
                    <button onClick={this.load}>加载</button>
                </div>
                <div>
                    <ul id="videobox" style={{marginLeft:"0",overflowX:"hidden", listStyle:"none", width:"100%", whiteSpace:"nowrap", position:"absolute"}}>
                        {this.state.items}
                    </ul>
                    <div style={{position:"relative", width:"100%"}}>
                        <button onClick={this.clickLeft} style={{position:"absolute", left:"0px", top:"150px", width:"20px",height:"40px",borderStyle:"none"}}>◀</button>
                        <button onClick={this.clickRight} style={{position:"absolute", right:"0px", top:"150px", width:"20px",height:"40px",borderStyle:"none"}}>▶</button>
                    </div>
                </div>
            </div>
        );
    }
}
