import React from 'react';

interface Istate{
    count:number
}


export default class Video extends React.Component<any,Istate>{
    public constructor(props:any) {
      super(props);
      this.clickHandler = this.clickHandler.bind(this);
    }

    public state:Readonly<Istate>={
        count:0
    }
    
    public clickHandler(){
        var file = document.getElementById("videofile") as HTMLInputElement
        var video = document.getElementById("videoplay") as HTMLVideoElement
        if(file.files !== null && file.files.length !== 0){
            var src = window.URL.createObjectURL(file.files[0])
            console.log(file.files[0])
            console.log(src)
            video.src = src
        }
        else{
            alert("无上传文件")
        }
        //     count:this.state.count+1,
        // })
        // console.log(this.state.count);
    }

    public loopPLAY(){
        var src = (document.getElementById("src1") as HTMLTableRowElement).textContent
        var video = document.getElementById("videoplay") as HTMLVideoElement
        console.log(src);
        if (src !==null){
            video.src = src 
            video.load()
            video.play()
        }
    }


    public render() {
      return (
        <div>
            <div>
                {/* 读取本地文件并播放 */}
                <input id="videofile" type="file"/>
                <input type="button" id="btplay" onClick={this.clickHandler} value="播放"/>    
            </div>
            <div>
                <table className="table" id="filedTable">
                    <tbody>
                        <tr>
                            <td>序号</td>
                            <td>视频文件地址</td>
                        </tr>
                        <tr>
                            <td>1</td>
                            <td id="src1">view-source:https://www.bilibili.com/video/av97525353?spm_id_from=333.5.b_6c6966655f66756e6e79.5</td>
                        </tr>
                        <tr>
                            <td>2</td>
                            <td id="src2">..\src\2.mp4</td>
                        </tr>
                    </tbody>
                </table>
            </div>
            <div>
                <meta name="viewport" content="width=device-width, initial-scale=1.0"/>
                <video controls autoPlay id="videoplay" width={"100%"} onEnded={this.loopPLAY}>
                    <source type="audio/mpeg"/>
                </video> 
            </div>
        </div>
      );
    }
  }
