import React from 'react';

interface Istate{
    count:number
    fdir?:any
}

export default class Video extends React.Component<any,Istate>{
    public constructor(props:any) {
      super(props);
      this.clickHandler = this.clickHandler.bind(this);
    }
    
    public state:Readonly<Istate>={
        count:0,
        fdir:0
    }

    public SubmitValidation(){
        let a =(document.getElementById("fileinput") as HTMLInputElement).value
        if (a == null|| a==""){
            alert("请选择文件！")
            return false
        }
        else{
            return true
        }

    }

    public clickHandler(){
        let a =(document.getElementById("fileinput") as HTMLInputElement)  //ts必须进行强制转换才能读取vlaue
        alert(a.value) 
        console.log(this.state.count,this.state.fdir)
        this.setState({
            count:this.state.count+1,
            fdir:a.value
        }) 
        console.log(this.state.count,this.state.fdir)
    }

    public render() {
      return (
        <div>
            <form action=""  target="uploader1" method="get" encType="multipart/form-data" id="uploadForm">
                 <input id="fileinput" type="file" onClick={this.clickHandler}/>
                 {/* <input type="submit" value="上传" onClick={this.clickHandler}/> */}
                 <button>上传</button>
            </form>
            <iframe name='upload' id='uploader1'></iframe>
        </div>
      );
    }
  }
